import React, { useEffect, useState } from 'react';
import axios from 'axios';

const JobOpportunities = ({ profileFilter, paygradeFilter }) => {
  const [jobOpportunities, setJobOpportunities] = useState([]);

  useEffect(() => {
    // Fetch job opportunities with filters from the backend API
    axios.get(`http://localhost:8001/jobopportunity?profile=${profileFilter}&paygrade=${paygradeFilter}`)
      .then(response => setJobOpportunities(response.data))
      .catch(error => console.error(error));
  }, [profileFilter, paygradeFilter]);

  return (
    <div>
      <h2>Job Opportunities</h2>
      <ul>
        {jobOpportunities.map(job => (
          <li key={job._id}>{job.title}</li>
        ))}
      </ul>
    </div>
  );
};

export default JobOpportunities;


function Login(props) {
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')

    function handleLogin(e) {
        e.preventDefault()
        // Code to handle login goes here
        props.toggle()
    }

    return (
        <div className="popup">
            <div className="popup-inner">
                <h2>Login</h2>
                <form onSubmit={handleLogin}>
                    <label>
                        Username:
                        <input type="text" value={username} onChange={e => setUsername(e.target.value)} />
                    </label>
                    <label>
                        Password:
                        <input type="password" value={password} onChange={e => setPassword(e.target.value)} />
                    </label>
                    <button type="submit">Login</button>
                </form>
                <button onClick={props.toggle}>Close</button>
            </div>
        </div>
    )
}
