// ViewApplicants.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import NavbarProducer from '../../components/navbar/NavbarProducer';
import styles from './ViewApplicants.module.css';

const ViewApplicants = ({ jobId }) => {
  const [applicants, setApplicants] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchApplicants = async () => {
      try {
        const response = await axios.get(`http://localhost:8001/jobs/applied?jobId=${jobId}`, {
          withCredentials: true,
        });

        const { success, data, message } = response.data;

        if (success) {
          setApplicants(data);
        } else {
          setError(message);
        }
      } catch (error) {
        console.error('Error fetching applicants:', error);
        setError('An error occurred while fetching applicants.');
      }
    };

    fetchApplicants();
  }, [jobId]);

  // const handleAccept = async (applicantId) => {
  //   // Implement the logic to accept the applicant with the given ID
  //   try {
  //     const response = await axios.post(`http://localhost:8001/jobs/acceptapplicant/${applicantId}`, {
  //       withCredentials: true,
  //     });

  //     const { success, message } = response.data;

  //     if (success) {
  //       // Refresh the list of applicants after accepting
  //       fetchApplicants();
  //       console.log(message);
  //     } else {
  //       console.error(message);
  //     }
  //   } catch (error) {
  //     console.error('Error accepting applicant:', error);
  //   }
  // };

  // const handleReject = async (applicantId) => {
  //   // Implement the logic to reject the applicant with the given ID
  //   try {
  //     const response = await axios.post(`http://localhost:8001/jobs/rejectapplicant/${applicantId}`, {
  //       withCredentials: true,
  //     });

  //     const { success, message } = response.data;

  //     if (success) {
  //       // Refresh the list of applicants after rejecting
  //       fetchApplicants();
  //       console.log(message);
  //     } else {
  //       console.error(message);
  //     }
  //   } catch (error) {
  //     console.error('Error rejecting applicant:', error);
  //   }
  // };

  return (
    <div className={styles.viewApplicantsContainer}>
      <NavbarProducer />
      {error ? (
        <p className={styles.errorMessage}>{error}</p>
      ) : (
        <div className={styles.applicantsContainer}>
          <h2>Applicants for Job</h2>
          {applicants && applicants.length > 0 ? (
            <ul>
              {applicants.map((applicant) => (
                <li key={applicant._id}>
                  <strong>Username:</strong> {applicant.userId.username}, <strong>SOP:</strong> {applicant.sopText}
                  {/* <button onClick={() => handleAccept(applicant._id)}>Accept</button> */}
                  <button onClick={() => handleReject(applicant._id)}>Reject</button>
                </li>
              ))}
            </ul>
          ) : (
            <p>No applicants found for this job.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default ViewApplicants;
