import React, { useState } from "react";
import {  useNavigate } from "react-router-dom";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import rightImage from '../../assets/images/home/rightImage.png';


const Description = () => {
  const navigate = useNavigate();
  const [inputValue, setInputValue] = useState({
    username: "",
    description: "",
    previousProjects: "",
    profileImage: null,
  });
  const { 
    username,
    description,
    previousProjects,
    profileImage
 } = inputValue;


  const handleOnChange = (e) => {
    const { name, value } = e.target;
    setInputValue({
      ...inputValue,
      [name]: value,
    });
  };

  const handlePhoto = (e) => {
    const { name, files } = e.target;
    setInputValue({
      ...inputValue,
      [name]: files[0],
    });
};

  const handleError = (err) =>
    toast.error(err, {
      position: "bottom-left",
    });
  const handleSuccess = (msg) =>
    toast.success(msg, {
      position: "bottom-left",
    });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {

        const formData = new FormData();
        Object.keys(inputValue).forEach((key) => {
        formData.append(key, inputValue[key]);
      });

      const { data } = await axios.post(
        "http://localhost:8001/description",formData,
       
        { withCredentials: true }
      );
      const { success, message } = data;
      if (success) {
        handleSuccess(message);
        setTimeout(() => {
          navigate("/dashboardfreelancer");
        }, 1000);
      } else {
        handleError(message);
      }
    } catch (error) {
      console.log(error);
    }
    setInputValue({
      username: "",
      description: "",
      previousProjects: "",
      profileImage: null,
    });
  };

  return (
    <>
    <div>
       <img src={rightImage} alt="home" className="right-Image" /> 
    </div>
    <div className="form_container">
      <h2>Login Account</h2>
      <form onSubmit={handleSubmit}>
        <div className="profile-photo-container">
            <label htmlFor="profile-photo">
              {profileImage ? (
                <img
                  src={URL.createObjectURL(profileImage)}
                  alt="profile"
                  className="profile-image"
                />
              ) : (
                <div className="profile-image-placeholder" onChange={handlePhoto}>+</div>
              )}
            </label>
            <input
              type="file"
              accept=".png, .jpg, .jpeg"
              name="profileImage"
              style={{ display: "none" }}
            />
           
        </div>

        <div>
          <label htmlFor="username">Username</label>
          <input
            type="text"
            name="username"
            placeholder="Enter your Username"
            onChange={handleOnChange}
          />
        </div>

        <div>
          <label htmlFor="description">Description</label>
          <input
            type="text"
            name="description"
            placeholder="Enter a brief about yourself"
            onChange={handleOnChange}
          />
        </div>

        <div>
          <label htmlFor="previousProjects">Projects</label>
          <input
            type="text"
            name="previousProjects"
            placeholder="Enter a brief about yourself"
            onChange={handleOnChange}
          />
        </div>

        <button type="submit">Submit</button>
      </form>
      <ToastContainer />
    </div>
    </>
    
  );
};

export default Description;
