import React from "react";
import NavbarProducer from "../../components/navbar/NavbarProducer";
import styles from './Dashboard.module.css'
import { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";


function CreateJob(props){
  const [inputValue, setInputValue] = useState({
    title: "",
    skills: "",
    jobType: "",
    duration: "",
    salary: "",
    deadline: "",
    positionsAvailable: "",
  });
  const { title,skills,jobType,duration,salary,deadline,positions } = inputValue;

  const handleOnChange = (e) => {
    const { name, value } = e.target;
    setInputValue({
      ...inputValue,
      [name]: value,
    });
  };

  const handleError = (err) =>
    toast.error(err, {
      position: "bottom-left",
    });
  const handleSuccess = (msg) =>
    toast.success(msg, {
      position: "bottom-right",
    });

  
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await axios.post(
        "http://localhost:9001/panel",
        {
          ...inputValue,
        },
        { withCredentials: true }
      );
      const { success, message } = data;
      if (success) {
        handleSuccess(message);
        props.addPanel(inputValue);      
        
      } else {
        handleError(message);
      }
    } catch (error) {
      console.log(error);
    }
    setInputValue({
      ...inputValue,
      topic: "",
      description: "",
    });
    props.toggle();
  };

  return (
      <div className={styles.popup}>
          <div className={styles.popup_inner}>
                  <h2>Create Panel</h2>
                  <form onSubmit={handleSubmit}>
                  <div>
                    <label htmlFor="topic">topic</label>
                    <input
                      type="topic"
                      name="topic"
                      value={topic}
                      placeholder="Enter your topic"
                      onChange={handleOnChange}

                    />
                  </div>
                  <div>
                    <label htmlFor="description">description</label>
                    <input
                      type="description"
                      name="description"
                      value={description}
                      placeholder="Enter your description"
                      onChange={handleOnChange}

                    />
                  </div>
                  <button type="submit">Create</button>
              </form>
              <button onClick={props.toggle}>Close</button>
          </div>
      </div>
  )
}
const DashboardProducer = () => {
  const [seen, setSeen] = useState(false)

  function togglePop () {
    setSeen(!seen);
};
  return (
    <>
      <NavbarProducer/>
      <div className={styles.dashpro}>
          <button onClick={togglePop}>Create Job</button>
          {seen ? <CreateJob toggle={togglePop} /> : null}
      </div>
    </>
    
  );
};
export default DashboardProducer ;
