import React from "react";
import NavbarProducer from "../../components/navbar/NavbarProducer";
import { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import styles from "./Dashboard.module.css";
import { FormControl, InputLabel, MenuItem, Select, TextField, Chip, Button, Typography } from '@mui/material';
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import DesktopDatePicker from '@mui/lab/DesktopDatePicker';


function CreateJob(props){
  const [inputValue, setInputValue] = useState({
    title: "",
    skills: "",
    jobType: "",
    duration: "",
    salary: "",
    deadline: "",
    positionsAvailable: "",
  });
  const { title,skills,jobType,duration,salary,deadline,positionsAvailable } = inputValue;

  const handleOnChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData)=>({
      ...inputValue,
      [name]: value,
    }));
  };

  const handleError = (err) =>
    toast.error(err, {
      position: "bottom-left",
    });
  const handleSuccess = (msg) =>
    toast.success(msg, {
      position: "bottom-right",
    });

  
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await axios.post(
        "http://localhost:8001/createjob",
        {
          ...inputValue,
        },
        { withCredentials: true }
      );
      const { success, message } = data;
      if (success) {
        handleSuccess(message);
        props.addJob(inputValue);      
        
      } else {
        handleError(message);
      }
    } catch (error) {
      console.log(error);
    }
    setInputValue({
      ...inputValue,
      title: "",
      skills: "",
      jobType: "",
      duration: "",
      salary: "",
      deadline: "",
      positionsAvailable: "",

    });
    props.toggle();
  };

  return (
      <div className={styles.popup}>
          <div className={styles.popup_inner}>
                  <h2>Create Job</h2>
                  <form onSubmit={handleSubmit}>
                  <div>
                    <label htmlFor="title">Title</label>
                    <input
                      type="string"
                      name="title"
                      value={title}
                      placeholder="Enter Job Title"
                      onChange={handleOnChange}

                    />
                  </div>
                  <div>
                    <label htmlFor="skills">Skills</label>
                    <input
                      type="string"
                      name="skills"
                      value={skills}
                      placeholder="Enter your skills"
                      onChange={handleOnChange}

                    />
                  </div>
                  <div>
                  <InputLabel htmlFor="jobType">Job Type</InputLabel>
                    <Select
                      labelId="jobType-label"
                      id="jobType"
                      value={jobType}
                      onChange={handleOnChange}
                      label="Job Type"
                      name="jobType"
                    >
                      <MenuItem value="fullTime">Full Time</MenuItem>
                      <MenuItem value="partTime">Part Time</MenuItem>
                      <MenuItem value="contract">Contract</MenuItem>
                      {/* Add more options as needed */}
                    </Select>
                  </div>
                  <div>
                    <label htmlFor="duration">Duration</label>
                    <input
                      type="string"
                      name="diration"
                      value={duration}
                      placeholder="Enter duration"
                      onChange={handleOnChange}

                    />
                  </div>
                  <div>
                    <label htmlFor="salary">Salary</label>
                    <input
                      type="salary"
                      name="salary"
                      value={salary}
                      placeholder="Enter salary"
                      onChange={handleOnChange}

                    />
                  </div>
                  <div>
                    <label htmlFor="positions">Positions Available</label>
                    <input
                      type="positions"
                      name="positions"
                      value={positionsAvailable}
                      placeholder="Enter positions available"
                      onChange={handleOnChange}

                    />
                  </div>
                  <div>
                    <label htmlFor="deadline">Deadline</label>
                    <input
                      type="deadline"
                      name="deadline"
                      value={deadline}
                      placeholder="Enter Application Deadline"
                      onChange={handleOnChange}

                    />
                  </div>
                  <button type="submit">Create</button>
              </form>
              <button onClick={props.toggle}>Close</button>
          </div>
      </div>
  )
}
const DashboardProducer = () => {
  const [seen, setSeen] = useState(false)
  const [jobs, setJobs] = useState([]);

  function togglePop () {
    setSeen(!seen);
  };
  const addJob = (job) => {
      const newJobs = [...jobs, job];
      setJobs(newJobs);
    }

  return (
    <>     
     <NavbarProducer/>
     <div>
                        <div className={styles.cardsContainer}>
                        {jobs.map((job, index) => (
                          <div key={index} className={styles.cards}>
                            <h3>{job.title}</h3>
                            <h3>{job.positionsAvailable}</h3>

                            <h3>{job.skills}</h3>
                            <h3>{job.jobType}</h3>
                            <h3>{job.duration}</h3>
                            <h3>{job.salary}</h3>
                            <h3>{job.deadline}</h3>
                          </div>
                        ))}
                        </div>
                        <button onClick={togglePop} className={styles.create}>Create</button>

                        {seen ? <CreateJob toggle={togglePop} addJob={addJob} /> : null}
    </div>                   

    </>
    
  );
};
export default DashboardProducer ;
