import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import rightImage from "../../assets/images/home/rightImage.png";
import profileImage from "../../assets/images/home/profile.png";

const Description = () => {
  const navigate = useNavigate();
  const [inputValue, setInputValue] = useState({
    username: "",
    description: "",
    previousProjects: "",
    profileImage: null,
  });

  const { username, description, previousProjects, profileImage } = inputValue;

  const handleOnChange = (e) => {
    const { name, value } = e.target;
    setInputValue({
      ...inputValue,
      [name]: value,
    });
  };

  const handlePhoto = (e) => {
    const { name, files } = e.target;
    setInputValue({
      ...inputValue,
      [name]: files[0],
    });
  };

  const handleError = (err) =>
    toast.error(err, {
      position: "bottom-left",
    });
  const handleSuccess = (msg) =>
    toast.success(msg, {
      position: "bottom-left",
    });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      Object.keys(inputValue).forEach((key) => {
        formData.append(key, inputValue[key]);
      });

      const { data } = await axios.post(
        "http://localhost:8001/description",
        formData,
        { withCredentials: true }
      );

      const { success, message } = data;
      if (success) {
        handleSuccess(message);
        setTimeout(() => {
          navigate("/dashboard");
        }, 1000);
      } else {
        handleError(message);
      }
    } catch (error) {
      console.log(error);
    }

    setInputValue({
      username: "",
      description: "",
      previousProjects: "",
      profileImage: null,
    });
  };

  return (
    <>
      <div>
        <img src={rightImage} alt="home" className="right-Image" />
      </div>
      <div className="form_container">
        <h2 className="login-account">Describe Yourself</h2>
        <form onSubmit={handleSubmit}>
          <div className="add-photo">
                <input
                  type="file"
                  accept=".png, .jpg, .jpeg"
                  name="profileImage"
                  id="profileImage"
                  onChange={handlePhoto}
                />
                
          </div>

          {profileImage && (
            <div>
              <img
                src={URL.createObjectURL(profileImage)}
                alt="selected-profile"
                className="selected-profile-image"
              />
            </div>
          )}

        <div>
          <label htmlFor="username">Username</label>
          <input
            type="text"
            name="username"
            value={username}
            placeholder="Enter your Username"
            onChange={handleOnChange}
          />
        </div>

        <div>
          <label htmlFor="description">Description</label>
          <input
            type="text"
            name="description"
            value={description}
            placeholder="Enter a brief about yourself"
            onChange={handleOnChange}
          />
        </div>

        <div>
          <label htmlFor="previousProjects">Projects</label>
          <input
            type="text"
            name="previousProjects"
            value={previousProjects}
            placeholder="Enter a brief about yourself"
            onChange={handleOnChange}
          />
        </div>

        <button type="submit">Submit</button>
      </form>
      <ToastContainer />
    </div>
    </>
    
  );
};

export default Description;
