import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import rightImage from '../../assets/images/home/rightImage.png';


const Description = () => {
  const navigate = useNavigate();
  const [inputValue, setInputValue] = useState({
    username: "",
    description: "",
    previousProjects: "",
    profileImage: "",
  });
  const { 
    username,
    description,
    previousProjects,
    profileImage
 } = inputValue;


  const handleOnChange = (e) => {
    const { name, value } = e.target;
    setInputValue({
      ...inputValue,
      [name]: value,
    });
  };

  const handlePhoto = (e) => {
    setNewAuthor({...newUser,photo:e.target.files[0]});
    console.log(newUser);
  };

  const handleError = (err) =>
    toast.error(err, {
      position: "bottom-left",
    });
  const handleSuccess = (msg) =>
    toast.success(msg, {
      position: "bottom-left",
    });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await axios.post(
        "http://localhost:9001/login",
        {
          ...inputValue,
        },
        { withCredentials: true }
      );
      const { success, message } = data;
      if (success) {
        handleSuccess(message);
        setTimeout(() => {
          navigate("/dashboard");
        }, 1000);
      } else {
        handleError(message);
      }
    } catch (error) {
      console.log(error);
    }
    setInputValue({
      ...inputValue,
      email: "",
      password: "",
    });
  };

  return (
    <>
    <div>
       <img src={rightImage} alt="home" className="right-Image" /> 
    </div>
    <div className="form_container">
      <h2>Login Account</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="profile-photo">Add Profile Photo</label>
          <input
            type="file"
            accept=".png. jpg, .jpeg"
            name="photo" 
            onChange={handlePhoto}
          />
        </div>

        <div>
          <label htmlFor="username">Username</label>
          <input
            type="text"
            name="username"
            value={username}
            placeholder="Enter your Username"
            onChange={handleOnChange}
          />
        </div>

        <div>
          <label htmlFor="description">Description</label>
          <input
            type="text"
            name="description"
            value={description}
            placeholder="Enter a brief about yourself"
            onChange={handleOnChange}
          />
        </div>

        <div>
          <label htmlFor="previousProjects">Projects</label>
          <input
            type="text"
            name="previousProjects"
            value={previousProjects}
            placeholder="Enter a brief about yourself"
            onChange={handleOnChange}
          />
        </div>

        <button type="submit">Submit</button>
        <span>
          Already have an account? <Link to={"/signup"}>Signup</Link>
        </span>
      </form>
      <ToastContainer />
    </div>
    </>
    
  );
};

export default Description;
