const handleSubmit = async (e) => {
  e.preventDefault();
  try {
      const { data } = await axios.post(
          "http://localhost:9001/password-reset",
          { ...inputValue },
          { withCredentials: true }
      );

      const { success, message } = data;
      if (success) {
          handleSuccess(message);
          setTimeout(() => {
              navigate("/reset");
          }, 1000);
      } else {
          handleError(message);
      }
  } catch (error) {
      console.error(error);

      // Handle the error and provide feedback to the user
      handleError("An error occurred during the password reset process.");
  }

  setInputValue({
      ...inputValue,
      email: ""
  });
};
