import React, { useEffect, useState } from 'react';
import Navbar from '../../components/navbar/NavbarFreelancer';
import axios from 'axios';
import styles from './Profile.module.css';

const ProfileFreelancer = () => {
  const [profileData, setProfileData] = useState({});
  const [error, setError] = useState('');
  

  
  useEffect(() => {
    const fetchProfileData = async () => {
      const userId = localStorage.getItem('_id');
      try {
        const token = sessionStorage.getItem('token');
        const { data } = await axios.get(
          `http://localhost:8001/profile/getprofile/${userId}`,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          },
          { withCredentials: true }
        );
  
        const { success, profile } = data;
        console.log(data);
        if (success) {
          setProfileData(profile);
        } else {
          setError("Failed to fetch profile data");
          console.error("Server response error:", data);
        }
      } catch (error) {
        setError("Failed to fetch profile data");
        console.log(error);
      }
    };
  
    fetchProfileData();
  }, []);

  return (
    <>
      <Navbar />
      <div className={styles.container}>
        <h2>Profile</h2>
        {error && <p>{error}</p>}
        {profileData && (
          <div className={styles.profileSection}>
            <img
              src={profileData.profileImage}
              alt="Profile"
              className={styles.profilePhoto}
            /> 
            <div className={styles.profileDetails}>
              <p>Name: {profileData.username}</p>
              <p>Email: {profileData.email}</p>
              <p>Contact: {profileData.contact}</p>
              <p>About You: {profileData.description}</p>
              <p>Job Profile: </p>
              <p>Portfolio: </p> 
              <p>Expected Paygrade: </p>
              <p>Previous Jobs: </p>
            </div> 
          </div>
        )}
        <button className={styles.editButton}>Complete Profile</button>
      </div>
    </>
  );
}


export default ProfileFreelancer;
