import React, { useEffect, useState } from 'react';
import axios from 'axios';

const JobOpportunities = ({ profileFilter, paygradeFilter }) => {
  const [jobOpportunities, setJobOpportunities] = useState([]);

  useEffect(() => {
    // Fetch job opportunities with filters from the backend API
    axios.get(`http://localhost:8001/jobopportunity?profile=${profileFilter}&paygrade=${paygradeFilter}`)
      .then(response => setJobOpportunities(response.data))
      .catch(error => console.error(error));
  }, [profileFilter, paygradeFilter]);

  return (
    <div>
      <h2>Job Opportunities</h2>
      <ul>
        {jobOpportunities.map(job => (
          <li key={job._id}>{job.title}</li>
        ))}
      </ul>
    </div>
  );
};

export default JobOpportunities;
