import React from 'react';
import NavbarProducer from '../../components/navbar/NavbarProducer';
import {useState} from 'react';
import axios from 'axios';
import { useEffect } from 'react';
import styles from './Profile.module.css';
import Navbar from '../../components/navbar/NavbarProducer';

const ProfileProducer = () => {
    const [profileData, setProfileData] = useState({});
    const [error, setError] = useState('');
    const [isEditing, setIsEditing] = useState(false);
    const [username, setusername] = useState('');
    const [contact, setcontact] = useState('');
    const [profilePhoto, setProfilePhoto] = useState('');

    const handleCompleteProfileClick = () => {
        setIsEditing(true);
      };

      const handleFormSubmit = async (event) => {
        event.preventDefault();
        const userId = localStorage.getItem('_id');
        try {
          const token = sessionStorage.getItem('token');
          const response = await axios.put(
            `http://localhost:8001/profile/updatepro/${userId}`,
            { username , contact , profilePhoto  },
            {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            },
    
            { withCredentials: true }
          );
          if (response.data.success) {
            setIsEditing(false);
            // Refresh profile data
          } else {
            console.error("Server response error:", response.data);
          }
        } catch (error) {
          console.error("Failed to update profile data", error);
        }
      };

      useEffect(() => {
        const fetchProfileData = async () => {
          const userId = localStorage.getItem('_id');
          try {
            const token = sessionStorage.getItem('token');
            const { data } = await axios.get(
              `http://localhost:8001/profile/getproducerprofile/${userId}`,
              {
                headers: {
                  Authorization: `Bearer ${token}`,
                },
              },
              { withCredentials: true }
            );
      
            const { success, profile } = data;
            if (success) {
              setProfileData(profile);
            } else {
              setError("Failed to fetch profile data");
              console.error("Server response error:", data);
            }
          } catch (error) {
            setError("Failed to fetch profile data");
            console.log(error);
          }
        };
      
        fetchProfileData();
      }, []);

      
      return (
        <>
          <Navbar />
          <div className={styles.container}>
            <h2>Profile</h2>
            {error && <p>{error}</p>}
            {profileData && (
              <div className={styles.profileSection}>
                <img
                  src={profileData.profileImage}
                  alt="Profile"
                  className={styles.profilePhoto}
                /> 
                <div className={styles.profileDetails}>
                  <p>Email: {profileData && profileData.userId && profileData.userId.email}</p>
                  {isEditing ? (
                <form onSubmit={handleFormSubmit}>
                  <label>
                    Name:
                    <input type="text" value={username} onChange={e => setusername(e.target.value)} />
                  </label>
                  <label>
                    Contact:
                    <input type="text" value={contact} onChange={e => setcontact(e.target.value)} />
                  </label>                  
                  <button type="submit">Submit</button>
                </form>
              ) : (
                <button className={styles.editButton} onClick={handleCompleteProfileClick}>Complete Profile</button>
              )}
                </div> 
              </div>
            )}
          </div>
        </>
      );
    };


export default ProfileProducer;
