import React, { useState , usEffect} from "react";
import { useNavigate } from "react-router-dom";
import styles from "./home.module.css";
import { useCookies } from "react-cookie";
import axios from "axios";

const Home = () => {

  const navigate = useNavigate();
  const [cookies, removeCookie] = useCookies([]);
  const [username, setUsername] = useState("");
  
  useEffect(() => {
    const verifyCookie = async () => {
      if (!cookies.token) {
        navigate("/login");
      }
      const { data } = await axios.post(
        "http://localhost:4000",
        {},
        { withCredentials: true }
      );
      const { status, user } = data;
      setUsername(user);
      return status
        ? toast(`Hello ${user}`, {
            position: "top-right",
          })
        : (removeCookie("token"), navigate("/login"));
    };
    verifyCookie();
  }, [cookies, navigate, removeCookie]);
  const Logout = () => {
    removeCookie("token");
    navigate("/signup");
  };
    return (
      <div className="onboarding-container">
        <div className="about-section">
          <p className={styles.name}>Campus Cohort</p>
          <p className={styles.about}>
          A dynamic digital space dedicated to inclusive education, skill development, and peer networking. We're here to break down barriers and provide students a peer community and connections they need to thrive in their academic and professional journeys.        </p>
        </div>
      </div>
    );
  };

  export default Home;
