import React, { useEffect, useState } from 'react';
import axios from 'axios';
import NavbarProducer from "../../components/navbar/NavbarProducer";
import styles from './People.module.css';
import { Button, Dialog, DialogTitle, DialogContent, Slider } from "@mui/material";
import { FormControl, InputLabel, MenuItem, Select, TextField,  Typography } from '@mui/material';



const People = () => {
  const [people, setPeople] = useState([]);



  // Fetch people from the database when the component mounts
  useEffect(() => {
    const tokenCookie = document.cookie.split('; ').find(row => row.startsWith('token'));
    const token = tokenCookie ? tokenCookie.split('=')[1] : null;
    
    const fetchpeople = async () => {
      try {
        const response = await axios.get("http://localhost:8001/people/getpeople", {

          headers: {
            Authorization: `Bearer ${token}`
          },
          withCredentials: true
        });
  
        const peopleData = response.data;
  
        if (Array.isArray(peopleData.people)) {
          setPeople(peopleData.people);
          localStorage.setItem('people', JSON.stringify(peopleData.people));
        } else {
          console.error('Invalid data received from the API:', peopleData);
        }
      } catch (error) {
        console.error("Error fetching people:", error);
      }
    };
  
    fetchpeople();
  }, []);

  return (
    <>
    <NavbarProducer/>
    
    <div className={styles.cardsContainer}>
        {people && people.map((people, index) => (
          <div key={index} className={styles.cards}>
            <h3>Username: @{people.username}</h3>
            <h3> {people.skills}</h3>
            <h3>people Type: {people.peopleType}</h3>
            <h3>Duration: {people.duration}</h3>
            <h3>Salary: {people.salary}</h3>
            <h3>Location:{people.location}</h3>

            <button className={styles.apply} onClick={handleApply}>Apply</button>
          </div>
        ))}
      </div>

      {/* Filter Popup */}
      <Dialog open={filterVisible} onClose={() => setFilterVisible(false)} className={styles.dialogContainer}>
        <div className={styles.filterContainer}>
        <DialogTitle>Filter Options</DialogTitle>
        <DialogContent className={styles.dialogContent}>

        <Typography variant="h6">Location</Typography>
          <FormControl  className={styles.formElement}>
            <InputLabel htmlFor="filterLocation">Select Location Type</InputLabel>
            <Select
              labelId="filterLocation-label"
              id="filterLocation"
              value={filterLocation || ""}
              onChange={(e) => setFilterLocation(e.target.value)}
              label="Select Location"
            >
              <MenuItem value="">All</MenuItem>
              <MenuItem value="onSite">On Site</MenuItem>
              <MenuItem value="workFromHome">Work From Home</MenuItem>
            </Select>
          </FormControl>
          


          {/* people Type Filter */}
          <Typography variant="h6">people Type</Typography>
          <FormControl  className={styles.formElement}>
            <InputLabel htmlFor="filterpeopleType">Select people Type</InputLabel>
            <Select
              labelId="filterpeopleType-label"
              id="filterpeopleType"
              value={filterpeopleType || ""}
              onChange={(e) => setFilterpeopleType(e.target.value)}
              label="Select people Type"
            >
              <MenuItem value="">All</MenuItem>
              <MenuItem value="fullTime">Full Time</MenuItem>
              <MenuItem value="partTime">Part Time</MenuItem>
              <MenuItem value="contract">Contract</MenuItem>
            </Select>
          </FormControl>

          {/* Salary Filter */}
          <Typography variant="h6">Salary</Typography>
          <div className={styles.sliderContainer}>
          <Slider
            value={filterSalary}
            onChange={(event, newValue) => setFilterSalary(newValue)}
            valueLabelDisplay="auto"
            min={10000}
            max={100000}
            step={10000}
          />
          </div>


           {/* Duration Filter */}
           <Typography variant="h6">Duration</Typography>
          <div className={styles.sliderContainer}>
          <Slider
            value={filterDuration}
            onChange={(event, newValue) => setFilterDuration(newValue)}
            valueLabelDisplay="auto"
            min={1}
            max={12}
            step={1}
          />
          </div>
         

          {/* Title Filter */}
          <Typography variant="h6">Title</Typography>
          <div className={styles.formElement}>
          <TextField
            label="Select Title"
            select
            value={filterTitle || ""}
            onChange={(e) => setFilterTitle(e.target.value)}
            variant="outlined"
            fullWidth
          >
            <MenuItem value="">All</MenuItem>
            <MenuItem value="Video Editor">Video Editor</MenuItem>
            <MenuItem value="Graphic Designer">Designer</MenuItem>
            <MenuItem value="Sound Engineer">Sound Engineer</MenuItem>
            {/* Add more titles as needed */}
          </TextField>
          </div>
         

          {/* Add other filters as needed */}
        </DialogContent>
        </div>
        
        <div className={styles.applyButtonContainer}>
        <Button variant="contained" color="primary" onClick={handleApplyFilters}>
          Apply
        </Button>
      </div>     
      </Dialog>


    </>
    
  );
};

export default People;
