// src/components/FreelancersList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import NavbarProducer from '../../components/navbar/NavbarProducer';

const FreelancersList = ({ profileFilter, paygradeFilter }) => {
  const [freelancers, setFreelancers] = useState([]);

  useEffect(() => {
    // Fetch available freelancers with filters from the backend API
    axios.get(`/api/freelancers?profile=${profileFilter}&paygrade=${paygradeFilter}`)
      .then(response => setFreelancers(response.data))
      .catch(error => console.error(error));
  }, [profileFilter, paygradeFilter]);

  return (
    <>
    <NavbarProducer/>
    <div>
      <h2>Available Freelancers</h2>
      <ul>
        {freelancers.map(freelancer => (
          <li key={freelancer._id}>{freelancer.name} - {freelancer.profile}</li>
        ))}
      </ul>
    </div>
    </>
    
  );
};

export default FreelancersList;
