import React, { useEffect, useState } from 'react';
import Navbar from '../../components/navbar/NavbarFreelancer';
import axios from 'axios';
import styles from './Profile.module.css';
// import { Button, Dialog, DialogContent, div, FormControl, InputLabel, TextField, Typography } from '@material-ui/core';



const ProfileFreelancer = () => {
  const [profileData, setProfileData] = useState({});
  const [error, setError] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [jobProfile, setJobProfile] = useState('');
  const [portfolio, setPortfolio] = useState('');
  const [expectedPaygrade, setExpectedPaygrade] = useState('');
  const [previousJobs, setPreviousJobs] = useState('');
    
  const handleCompleteProfileClick = () => {
    setIsEditing(true);
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    const userId = localStorage.getItem('_id');
    try {
      const token = sessionStorage.getItem('token');
      const response = await axios.put(
        `http://localhost:8001/profile/update/${userId}`,
        { jobProfile, portfolio, expectedPaygrade, previousJobs },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        },

        { withCredentials: true }
      );
      if (response.data.success) {
        setIsEditing(false);
        // Refresh profile data
      } else {
        console.error("Server response error:", response.data);
      }
    } catch (error) {
      console.error("Failed to update profile data", error);
    }
  };

  
  useEffect(() => {
    const fetchProfileData = async () => {
      const userId = localStorage.getItem('_id');
      try {
        const token = sessionStorage.getItem('token');
        const { data } = await axios.get(
          `http://localhost:8001/profile/getprofile/${userId}`,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          },
          { withCredentials: true }
        );
  
        const { success, profile } = data;
        if (success) {
          setProfileData(profile);
        } else {
          setError("Failed to fetch profile data");
          console.error("Server response error:", data);
        }
      } catch (error) {
        setError("Failed to fetch profile data");
        console.log(error);
      }
    };
  
    fetchProfileData();
  }, []);

  return (
    <>
      <Navbar />
      <div className={styles.container}>
        {error && <p>{error}</p>}
        {profileData && (
          <div className={styles.profileSection}>
            <img
              src={profileData && profileData.profileImage ? `http://localhost:8001/${profileData.profileImage.replace(/\\/g, '/')}` : ''}
              alt="Profile"
              className={styles.profilePhoto}
            />
            <div className={styles.profileDetails}>
              <p>Name: {profileData.username}</p>
              <p>Email: {profileData && profileData.userId && profileData.userId.email}</p>
              <p>Contact: {profileData.contact}</p>
              <p>About You: {profileData.description}</p>
              <p>Job Profile:{profileData.jobProfile} </p>
              <p>Portfolio: {profileData.portfolio}</p> 
              <p>Expected Paygrade: {profileData.expectedPaygrade}</p>
              <p>Previous Jobs: {profileData.previousJobs}</p>
              <button className={styles.editButton} onClick={handleCompleteProfileClick}>Complete Profile</button>

              <div open={isEditing} onClose={() => setIsEditing(false)} className={styles.dialogContainer}>
                <div className={styles.profileContainer}>
                  <div>Complete Profile</div>
                    <DialogContent className={styles.dialogContent}>
                      <Typography variant="h6">Job Profile</Typography>
                      <div>
                      <FormControl  className={styles.formElement}>
                        <InputLabel htmlFor="jobProfile">Enter Job Profile</InputLabel>
                        <TextField
                          labelId="JobProfile-label"
                          id="JobProfile"
                          value={jobProfile || ""}
                          onChange={(e) => setJobProfile(e.target.value)}
                          label="Enter Job Profile"
                        >
                        </TextField>
                      </FormControl>
                      </div>
                      

                      <Typography variant="h6">Portfolio</Typography>
                      <div>
                      <FormControl  className={styles.formElement}>
                        <InputLabel htmlFor="portfolio">Enter Portfolio Link </InputLabel>
                        <TextField
                          labelId="portfolio"
                          id="portfolio"
                          value={portfolio || ""}
                          onChange={(e) => setPortfolio(e.target.value)}
                          label="Enter Portfolio Link"
                        >
                        </TextField>
                      </FormControl>
                      </div>
                      

                      
                      <Typography variant="h6">Expected Paygrade</Typography>
                      <div>
                      <FormControl  className={styles.formElement}>
                        <InputLabel htmlFor="portfolio">Enter Expected Paygrade </InputLabel>
                        <TextField
                          labelId="paygrade"
                          id="paygrade"
                          value={expectedPaygrade || ""}
                          onChange={(e) => setExpectedPaygrade(e.target.value)}
                          label="Enter Expected Paygrade"
                        >
                        </TextField>
                      </FormControl>
                      </div>
                      

                      
                      <Typography variant="h6">Previous Jobs</Typography>
                      <div>
                      <FormControl  className={styles.formElement}>
                        <InputLabel htmlFor="portfolio">Enter Your Previous Jobs </InputLabel>
                        <TextField
                          labelId="previousJobs"
                          id="previousJobs"
                          value={previousJobs || ""}
                          onChange={(e) => setPreviousJobs(e.target.value)}
                          label="Enter Your Previous Jobs"
                        >
                        </TextField>
                      </FormControl>
                      </div>
                    </DialogContent>
                    <div className={styles.applyButtonContainer}>
                    <Button variant="contained" color="primary" onClick={handleFormSubmit}>
                      Apply
                    </Button>
                   </div> 
                  </div>
                      
              </div>
                              
          
              
            </div> 
          </div>
        )}
      </div>
    </>
  );
}


export default ProfileFreelancer;
