import React from "react";
import NavbarProducer from "../../components/navbar/NavbarProducer";
import { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import styles from "./Dashboard.module.css";


function CreateJob(props){
  const [inputValue, setInputValue] = useState({
    title: "",
    skills: "",
    jobType: "",
    duration: "",
    salary: "",
    deadline: "",
    positionsAvailable: "",
  });
  const { title,skills,jobType,duration,salary,deadline,positionsAvailable } = inputValue;

  const handleOnChange = (e) => {
    const { name, value } = e.target;
    setInputValue({
      ...inputValue,
      [name]: value,
    });
  };

  const handleError = (err) =>
    toast.error(err, {
      position: "bottom-left",
    });
  const handleSuccess = (msg) =>
    toast.success(msg, {
      position: "bottom-right",
    });

  
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await axios.post(
        "http://localhost:8001/createjob",
        {
          ...inputValue,
        },
        { withCredentials: true }
      );
      const { success, message } = data;
      if (success) {
        handleSuccess(message);
        props.addPanel(inputValue);      
        
      } else {
        handleError(message);
      }
    } catch (error) {
      console.log(error);
    }
    setInputValue({
      ...inputValue,
      title: "",
      skills: "",
      jobType: "",
      duration: "",
      salary: "",
      deadline: "",
      positionsAvailable: "",

    });
    props.toggle();
  };

  return (
      <div className={styles.popup}>
          <div className={styles.popup_inner}>
                  <h2>Create Panel</h2>
                  <form onSubmit={handleSubmit}>
                  <div>
                    <label htmlFor="title">Title</label>
                    <input
                      type="title"
                      name="title"
                      value={title}
                      placeholder="Enter Job Title"
                      onChange={handleOnChange}

                    />
                  </div>
                  <div>
                    <label htmlFor="skills">Skills</label>
                    <input
                      type="skills"
                      name="skills"
                      value={skills}
                      placeholder="Enter your skills"
                      onChange={handleOnChange}

                    />
                  </div>
                  <div>
                    <label htmlFor="jobType">Job Type</label>
                    <input
                      type="jobType"
                      name="jobType"
                      value={jobType}
                      placeholder="Enter Job Type"
                      onChange={handleOnChange}

                    />
                  </div>
                  <div>
                    <label htmlFor="duration">Duration</label>
                    <input
                      type="duration"
                      name="diration"
                      value={duration}
                      placeholder="Enter duration"
                      onChange={handleOnChange}

                    />
                  </div>
                  <div>
                    <label htmlFor="salary">Salary</label>
                    <input
                      type="salary"
                      name="salary"
                      value={salary}
                      placeholder="Enter salary"
                      onChange={handleOnChange}

                    />
                  </div>
                  <div>
                    <label htmlFor="positions">Positions Available</label>
                    <input
                      type="positions"
                      name="positions"
                      value={positionsAvailable}
                      placeholder="Enter positions available"
                      onChange={handleOnChange}

                    />
                  </div>
                  <div>
                    <label htmlFor="deadline">Deadline</label>
                    <input
                      type="deadline"
                      name="deadline"
                      value={deadline}
                      placeholder="Enter Application Deadline"
                      onChange={handleOnChange}

                    />
                  </div>
                  <button type="submit">Create</button>
              </form>
              <button onClick={props.toggle}>Close</button>
          </div>
      </div>
  )
}
const DashboardProducer = () => {
  const [seen, setSeen] = useState(false)
  const [jobs, setJobs] = useState([]);

  function togglePop () {
    setSeen(!seen);

  const addJob = (panel) => {
      const newJobs = [...jobs, job];
      setJobs(newJobs);
    }
};
  return (
    <>     
     <NavbarProducer/>
     <div>
                        <div className={styles.cardsContainer}>
                        {jobs.map((job, index) => (
                          <div key={index} className={styles.cards}>
                            <h3>{job.topic}</h3>
                            <p>{panel.description}</p>
                          </div>
                        ))}
                        </div>
                        <button onClick={togglePop} className={styles.create}>Create</button>

                        {seen ? <CreateJob toggle={togglePop} addJob={addJob} /> : null}
    </div>                   

    </>
    
  );
};
export default DashboardProducer ;
