import React, { useState } from 'react';
import axios from 'axios';

const JobForm = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [paygrade, setPaygrade] = useState('');
  const [estimatedTime, setEstimatedTime] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Create a new job on the backend
      const response = await axios.post('http://localhost:8001/job-form', {
        title,
        description,
        paygrade,
        estimatedTime,
      });

      // Handle the response or navigate to another page
      console.log('Job created:', response.data);

      // Clear the form after successful submission
      setTitle('');
      setDescription('');
      setPaygrade('');
      setEstimatedTime('');
    } catch (error) {
      console.error('Error creating job:', error);
    }
  };

  return (
    <div>
      <h2>Create New Job</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
        </label>
        <label>
          Description:
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} required />
        </label>
        <label>
          Paygrade:
          <input type="text" value={paygrade} onChange={(e) => setPaygrade(e.target.value)} required />
        </label>
        <label>
          Estimated Time (hours):
          <input type="number" value={estimatedTime} onChange={(e) => setEstimatedTime(e.target.value)} required />
        </label>
        <button type="submit">Create Job</button>
      </form>
    </div>
  );
};

export default JobForm;
